mapper.select:
SELECT mr.*,m.MODEL_NAME,m.MODEL_DESC FROM T_MODEL_RESULT mr
LEFT JOIN T_MODEL m on mr.MODEL_ID = m.ID
WHERE 1 = 1

    and m.MODEL_NAME like '%'||#{modelName}||'%'


    and mr.WARN_FLAG = #{warnFlag}


    and mr.PASSPORTNO like '%'||#{certNo}||'%'


    and mr.FLTNO like '%'||#{fltno}||'%'


    and mr.FLTNODATE >= #{fltDateStart}


    and mr.FLTNODATE <= #{fltDateEnd}

SELECT HOUR,COUNT(1) COUNT FROM ( SELECT TO_NUMBER (TO_CHAR (CREATE_TIME, 'hh24')) HOUR FROM T_MODEL_RESULT WHERE TO_CHAR(CREATE_TIME,'YYYY-MM-DD')= CURDATE()
AND MODEL_ID = #{id}
) GROUP BY HOUR ORDER BY HOUR

SELECT CONCAT(A.BUSI_HOUR,A.MODEL_ID) KEY,A.BUSI_HOUR HOUR,A.MODEL_ID,B.MODEL_NAME, ROUND(SUM(A.DURATION)/COUNT(1),0) VALUE FROM T_MODEL_RESULT_COUNT A LEFT JOIN T_MODEL B ON A.MODEL_ID=B.ID WHERE A.BUSI_DATE = TO_CHAR(TRUNC(SYSDATE),'YYYYMMDD')
AND A.MODEL_ID = #{id}
group by A.BUSI_HOUR,A.MODEL_ID,B.MODEL_NAME ORDER BY A.BUSI_HOUR,A.MODEL_ID
